import React, { useState, useEffect, useCallback } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import io from 'socket.io-client';
import 'leaflet/dist/leaflet.css';

// Define vehicle styles for different vehicle IDs
const vehicleStyles = {
  vehicle1: {
    icon: L.icon({
      iconUrl: '/images/tractor_2548747.png',
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32]
    }),
    pathColor: 'blue'
  },
  vehicle2: {
    icon: L.icon({
      iconUrl: '/images/tractor-color-icon.png',
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32]
    }),
    pathColor: 'red'
  },
  vehicle3: {
    icon: L.icon({
      iconUrl: '/images/vecteezy_construction-vehicle-3d-render-clipart_31614137.png',
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32]
    }),
    pathColor: 'green'
  }
};

const defaultStyle = {
  icon: L.icon({
    iconUrl: '/images/tractor_2548747.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
  }),
  pathColor: 'red'
};

// Set initial vehicle data with starting coordinates
const initialVehicles = {
  vehicle1: {
    id: "vehicle1",
    name: "Vehicle 1",
    currentPosition: { lat: 22.317094, lng: 87.314139 },
    lastUpdateTime: Date.now(),
    path: []
  },
  vehicle2: {
    id: "vehicle2",
    name: "Vehicle 2",
    currentPosition: { lat: 22.315632, lng: 87.315678 },
    lastUpdateTime: Date.now(),
    path: []
  },
  vehicle3: {
    id: "vehicle3",
    name: "Vehicle 3",
    currentPosition: { lat: 22.318920, lng: 87.313456 },
    lastUpdateTime: Date.now(),
    path: []
  }
};

function VehicleTracker() {
  // Initialize vehicles state with initialVehicles
  const [vehicles, setVehicles] = useState(initialVehicles);

  // Process data received from Firebase via Socket.IO
  const processFirebaseData = useCallback((data) => {
    if (!data) return;

    const updatedVehicles = { ...vehicles };

    Object.keys(data).forEach(vehicleId => {
      if (!data[vehicleId]) return;

      const vehicleData = data[vehicleId];
      const current = vehicleData.current;
      const path = vehicleData.path;

      if (current && current.latitude && current.longitude) {
        // Initialize vehicle if it doesn't exist
        if (!updatedVehicles[vehicleId]) {
          updatedVehicles[vehicleId] = {
            id: vehicleId,
            name: vehicleId,
            path: []
          };
        }

        // Update current position
        updatedVehicles[vehicleId].currentPosition = {
          lat: parseFloat(current.latitude),
          lng: parseFloat(current.longitude)
        };
        updatedVehicles[vehicleId].lastUpdateTime = current.timestamp || Date.now();

        // Process path data
        if (Array.isArray(path)) {
          // Convert Firebase path format to map-compatible format
          updatedVehicles[vehicleId].path = path.map(point => ({
            lat: parseFloat(point.latitude),
            lng: parseFloat(point.longitude),
            timestamp: point.timestamp
          }));
        }
      }
    });

    setVehicles(updatedVehicles);
  }, [vehicles]);

  useEffect(() => {
    // Connect to Socket.IO server
    const socket = io('http://localhost:8080');

    socket.on('connect', () => {
      console.log('Connected to server');
    });

    socket.on('gpsData', (data) => {
      console.log('Received GPS data:', data);
      processFirebaseData(data);
    });

    // Cleanup on unmount
    return () => {
      socket.disconnect();
    };
  }, [processFirebaseData]);

  return (
    <div className="map-container" style={{ height: '100vh', width: '100%' }}>
      <MapContainer 
        center={[22.317094, 87.314139]} 
        zoom={15} 
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {Object.keys(vehicles).map(id => {
          const vehicle = vehicles[id];
          const style = vehicleStyles[id] || defaultStyle;

          // Get marker position
          const pos = vehicle.currentPosition;
          if (!pos || pos.lat == null || pos.lng == null) return null;

          // Prepare path data for polyline
          let pathPositions = [];

          // Add path points from history
          if (vehicle.path && vehicle.path.length > 0) {
            pathPositions = vehicle.path
              .filter(p => p && p.lat != null && p.lng != null)
              .map(p => [p.lat, p.lng]);
          }

          // Add current position to the path if needed
          if (pathPositions.length === 0 || 
              pathPositions[pathPositions.length-1][0] !== pos.lat || 
              pathPositions[pathPositions.length-1][1] !== pos.lng) {
            pathPositions.push([pos.lat, pos.lng]);
          }

          return (
            <React.Fragment key={id}>
              {/* Render path as polyline */}
              {pathPositions.length > 1 && (
                <Polyline 
                  positions={pathPositions} 
                  color={style.pathColor} 
                  weight={3} 
                  opacity={0.7}
                />
              )}

              {/* Render vehicle marker */}
              <Marker position={[pos.lat, pos.lng]} icon={style.icon}>
                <Popup>
                  <div>
                    <h3>{vehicle.name}</h3>
                    <p>Last Update: {new Date(vehicle.lastUpdateTime).toLocaleString()}</p>
                    <p>Path Points: {pathPositions.length}</p>
                    <p>Position: {pos.lat.toFixed(6)}, {pos.lng.toFixed(6)}</p>
                  </div>
                </Popup>
              </Marker>
            </React.Fragment>
          );
        })}
      </MapContainer>
    </div>
  );
}

export default VehicleTracker;
